		public function userCheck($username){

			//init DB
			$con = new database();
			$db = $con>connection();

			$stmt = $db>prepare("SELECT * FROM members WHERE username = :input");
			$stmt>bindParam('':input'', $username, PDO::PARAM_STR);
			$stmt>execute();

