		match = re.findall("^[azAZ09]+$" , str)
		//Check for alphanumeric
		if match:
			return True
		else:
			return False

	def isAlpha(str):
		match = re.findall("^[azAZ]+$" , str)
		//Check for alpha
		if match:
			return True
		else:
			return False

