    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.4.1','Verify that files obtained from untrusted sources are stored outside the web root, with limited permissions, preferably with strong validation.',922,23,'False',227,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.4.2','Verify that files obtained from untrusted sources are scanned by antivirus scanners to prevent upload of known malicious content.',509,23,'False',226,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.5.1','Verify that the web tier is configured to serve only files with specific file extensions to prevent unintentional information and source code leakage. For example, backup files (e.g. .bak); temporary working files (e.g. .swp); compressed files (.zip, .tar.gz, etc) and other extensions commonly used by editors should be blocked unless required.',552,23,'False',288,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.5.2','Verify that direct requests to uploaded files will never be executed as HTML/JavaScript content.',434,23,'False',227,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.6.1','Verify that the web or application server is configured with a whitelist of resources or systems to which the server can send requests or load data/files from.',918,23,'False',999,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.0','API and Web Service Verification Requirements',0,0,'False',0,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.1.1','Verify that all application components use the same encodings and parsers to avoid parsing attacks that exploit different URI or file parsing behavior that could be used in SSRF and RFI attacks.',116,29,'False',999,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.1.2','Verify that access to administration and management functions is limited to authorized administrators.',419,29,'False',999,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.1.3','Verify API URLs do not expose sensitive information, such as the API key, session tokens etc.',598,29,'False',91,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.2.1','Verify that enabled RESTful HTTP methods are a valid choice for the user or action, such as preventing normal users using DELETE or PUT on protected API or resources.',650,29,'False',129,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.2.2','Verify that JSON schema validation is in place and verified before accepting input.',20,29,'False',286,1);
