    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('14.4.3','Verify that a content security policy (CSPv2) is in place that helps mitigate impact for XSS attacks like HTML, DOM, JSON, and JavaScript injection vulnerabilities.',1021,28,'False',178,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('14.4.4','Verify that all responses contain X-Content-Type-Options: nosniff.',116,28,'False',193,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('14.4.5','Verify that HTTP Strict Transport Security headers are included on all responses and for all subdomains, such as Strict-Transport-Security: max-age=15724800; includeSubdomains.',523,28,'False',192,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('14.4.6','Verify that a suitable "Referrer-Policy" header is included, such as "no-referrer" or "same-origin".',116,28,'False',282,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('14.4.7','Verify that a suitable X-Frame-Options or Content-Security-Policy: frame-ancestors header is in use for sites where content should not be embedded in a third-party site.',346,28,'False',20,1);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('14.5.1','Verify that the application server only accepts the HTTP methods in use by the application or API, including pre-flight OPTIONS.',749,28,'False',129,1);
