    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('1.2','Authentication Architectural Requirements',0,27,'FALSE',0,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('1.2.1','Verify the use of unique or special low-privilege operating system accounts for all application components, services, and servers.',250,27,'FALSE',126,2);
