    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('11.1.4','Verify the application has sufficient anti-automation controls to detect and protect against data exfiltration, excessive business logic requests, excessive file uploads or denial of service attacks.',770,30,'FALSE',999,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('11.1.5','Verify the application has business logic limits or validation to protect against likely business risks or threats, identified using threat modelling or similar methodologies.',841,30,'TRUE',164,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('11.1.6','Verify the application does not suffer from "time of check to time of use" (TOCTOU) issues or other race conditions for sensitive operations.',367,30,'TRUE',293,2);
