    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('11.1.8','Verify the application has configurable alerting when automated attacks or unusual activity is detected.',390,30,'FALSE',999,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.0','File and Resources Verification Requirements',0,0,'FALSE',0,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.1.1','Verify that the application will not accept large files that could fill up storage or cause a denial of service attack.',400,23,'FALSE',13,2);
