    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.3.6','Verify that the application does not include and execute functionality from untrusted sources, such as unverified content distribution networks, JavaScript libraries, node npm libraries, or server-side DLLs.',829,23,'FALSE',13,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.4.1','Verify that files obtained from untrusted sources are stored outside the web root, with limited permissions, preferably with strong validation.',922,23,'FALSE',227,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.4.2','Verify that files obtained from untrusted sources are scanned by antivirus scanners to prevent upload of known malicious content.',509,23,'FALSE',226,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.5.1','Verify that the web tier is configured to serve only files with specific file extensions to prevent unintentional information and source code leakage. For example, backup files (e.g. .bak); temporary working files (e.g. .swp); compressed files (.zip, .tar.gz, etc) and other extensions commonly used by editors should be blocked unless required.',552,23,'FALSE',288,2);
    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('12.5.2','Verify that direct requests to uploaded files will never be executed as HTML/JavaScript content.',434,23,'FALSE',227,2);
