    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.1.1','Verify that all application components use the same encodings and parsers to avoid parsing attacks that exploit different URI or file parsing behavior that could be used in SSRF and RFI attacks.',116,29,'FALSE',999,2);
