    INSERT OR REPLACE INTO `checklists_kb` (`checklist_id`, `content`, `cwe`, `question_id`, `include_always`, `kb_id`, `checklist_type`) VALUES ('13.2.4','Verify that REST services have anti-automation controls to protect against excessive calls, especially if the API is unauthenticated.',779,29,'FALSE',116,2);
